mapper.delete:
delete from cartitem
where mid=#{mid}
and pcode=#{pcode}
and pcolor=#{pcolor}
and psize=#{psize}

mapper.select:
		select c.pcode, p.pimage1, p.pbrand, p.pname, c.pcolor, c.psize, p.pprice, c.pquantity
		from cartitem c, product p
		where c.pcode = p.pcode and c.pcolor = p.pcolor and c.psize = p.psize
		and c.mid = #{mid}
		and c.pcode in

            #{pcode}

		and c.pcolor in

            #{pcolor}

		and c.psize in

            #{psize}
